package pac1;

public class TypeCasting1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * int i=(int)'g'; // Widning process System.out.println("value is " + i);
		 * 
		 * char c=(char)7;// narrowving process System.out.println("value of c is " +
		 * c);
		 */
		for(int i=48;i<=57;i++)
		{
			char c=(char)i;
			System.out.println(c);
		}
      
    
}
}